import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("");

  const dateFilterHandler = (year) => {
    setFilteredYear(year);
  };

  const filteredExpenses = (expensesList) => {
    return expensesList.filter(
      (exp) => exp.date.getFullYear() === parseInt(filteredYear)
    );
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onFilterSelected={dateFilterHandler}
        />
        {filteredExpenses(props.expenses).map((exp) => (
          <ExpenseItem
            key={exp.id}
            title={exp.title}
            amount={exp.amount}
            date={exp.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
